mapper.insert:
/* BoardMapper.insertBoard */
insert into tb_board_info
	a.bd_title
) values (
	#{bd_title}
)

mapper.update:
/* BoardMapper.updateBoard */
update tb_board_info
set
	a.bd_title = #{bd_title}
where a.bd_seq = #{bd_seq}

/* BoardMapper.updateDeleteBoard */
update tb_board_info
set
	a.del_yn = 'Y'
where a.bd_seq = #{bd_seq}
